                        p_mt_less_17_given_tt[i] *
                        pr_not_see_16_twice[i] *
                        std::pow(pr_not_see_0_twice[i], num_orange) *
                        std::pow(1 - pr_not_see_0_twice[i], num_yellow - 1);
